FROM employee a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.companyid = #{company.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sex = #{sex}


		AND a.identityno LIKE 
			'%'||#{identityno}||'%'
			'%'+#{identityno}+'%'
			concat('%',#{identityno},'%')


		AND a.residenceplace = #{residenceplace}


		AND a.martitalstatus = #{martitalstatus}


		AND a.education = #{education}


		AND a.worktitle = #{worktitle}


		AND a.ifApply = #{ifApply}


		AND a.ifApplyAudit = #{ifApplyAudit}


		AND a.reward = #{reward}



${sqlMap.dsf}


